import React, { Component } from 'react';
import styles from './Card.module.css';
import flow from 'lodash/flow';

import { DragSource, DropTarget } from 'react-dnd'

const Types = {
    ITEM: 'toy'
}


const itemSource = {
    beginDrag(props) {
        return {
            id: props.id,
            originalIndex: props.findCard(props.id).index,
        }
    },
    endDrag(props, monitor, component) {
        const { id: droppedId, originalIndex } = monitor.getItem()
        if (!monitor.didDrop()) {
            props.moveCard(droppedId, originalIndex)
        }
        const { moveCardToList } = props;
        const cardName = props.cardName;
        const currentList = props.currentList;
        const droppedList = monitor.getDropResult().droppedList;
        if(currentList !== droppedList){
            moveCardToList(cardName, currentList, droppedList);
        }
    }
}

const box = {
    drop( props, monitor, component: React.Component | null) {
        if (!component) {
            return
        }

        const droppedList = props.droppedList;
        return ({
            droppedList
        });

    },
    canDrop() {
        return false
    },
    hover(props: CardProps, monitor: DropTargetMonitor) {
        const { id: draggedId } = monitor.getItem()
        const { id: overId } = props

        if (draggedId !== overId) {
            const { index: overIndex } = props.findCard(overId)
            props.moveCard(draggedId, overIndex)
        }
    }
}

const collectDrag = (connect, monitor) =>{
    return {
        connectDragSource: connect.dragSource(),
        isDragging: monitor.isDragging()
    }
}

const collectDrop = (connect, monitor) => {
    return {
        connectDropTarget: connect.dropTarget(),
        isOverCurrent: monitor.isOver({ shallow: true })
    }
}

class Card extends Component {

    render() {
        const { isDragging, connectDragSource, connectDropTarget, isOverCurrent} = this.props
        let className = styles.Card
        if(isDragging){
            className=styles.CardDragging
        }
        if(isOverCurrent){
            className=styles.CardDrop
        }
        return connectDragSource(connectDropTarget(
                <div className={className}>
                    {this.props.cardName}
                </div>
        ))
    }
}

export default flow(
    DragSource(Types.ITEM, itemSource, collectDrag),
    DropTarget(Types.ITEM, box, collectDrop)
)(Card)



