import React, { Component } from 'react';
import styles from './Card.module.css';

import { DragSource, connectDragPreview } from 'react-dnd';

const Types = {
    ITEM: 'toy'
}

const itemSource = {
    beginDrag(props) {
        return {};
    },
    endDrag(props, monitor, component) {
        if (!monitor.didDrop()) {
            return;
        }
        const { moveCardToList } = props;
        const cardName = props.cardName;
        const currentList = props.currentList;
        const droppedList = monitor.getDropResult().droppedList;
        if(currentList !== droppedList){
            moveCardToList(cardName, currentList, droppedList);
        }
    }
}

function collect(connect, monitor) {
    return {
        connectDragSource: connect.dragSource(),
        connectDragPreview: connect.dragPreview(),
        isDragging: monitor.isDragging()
    }
}

 class Card extends Component {

    componentDidMount() {
        this.props.connectDragPreview(<div> YO! </div>);
    }

     render() {
        const { isDragging, connectDragSource} = this.props
        return connectDragSource(
            <div 
                className={isDragging ? styles.CardDragging: styles.Card}>
            {this.props.cardName}</div>
        )
     }
}

export default DragSource(Types.ITEM, itemSource, collect)(Card)
