import React, { Component } from 'react';
import { connect } from 'react-redux';
import styles from './List.module.css';
import Card from '../../components/Card/Card';
import InputCard from '../InputCard/InputCard';
import * as listActions from '../../store/actions/lists';

import { DropTarget } from 'react-dnd'

const Types = {
    ITEM: 'card'
}

const cardTarget = {
    drop() {}
}

const collectDrop = (connect, monitor) => {
    return {
        connectDropTarget: connect.dropTarget()
    }
}

class List extends Component {

    findCardHandler = id => {
        const card = this.props.cards.filter(c => `${c.id}` === id)[0]
        return {
            card,
            index: this.props.cards.indexOf(card)
        }
    }

    render(){
        const { connectDropTarget } = this.props
        const displayCards = this.props.cards.map((cardName, index)=> {
            return (
                <Card key={cardName+index}
                        id ={index}
                        cardName={cardName} 
                        currentList={this.props.listName}
                        findCard={this.findCardHandler} 
                        moveCard={this.props.onCardMoved}/>
            )
        })
        return connectDropTarget(
            <div className={styles.List}>
                <h4>{this.props.listName}</h4>
                {displayCards}
                <InputCard createNewCard={this.props.onCardAdded} 
                            cards={this.props.cards} 
                            list={this.props.listName}/>
            </div>
        );
    }
}

const mapDispatchToProps= dispatch => {
    return {
        onCardAdded: (cardName, listName) => dispatch(listActions.addCard(cardName, listName)),
        onCardMoved: (cardName, currentList, droppedList) => dispatch(listActions.moveCard(cardName, currentList, droppedList)),
    }
}

export default DropTarget(Types.ITEM, cardTarget, collectDrop)(connect(null, mapDispatchToProps)(List));
