import * as React from 'react'
import {
    DragSource,
    DropTarget,
    DragSourceMonitor,
    DropTargetMonitor,
} from 'react-dnd'

import styles from './Card.module.css';

const Types = {
    ITEM: 'card'
}

const cardSource = {
    beginDrag(props: CardProps) {
        return {
            id: props.id,
            originalIndex: props.findCard(props.id).index,
        }
    },

    endDrag(props: CardProps, monitor: DragSourceMonitor) {
        const { id: droppedId, originalIndex } = monitor.getItem()
        const didDrop = monitor.didDrop()
        const listId = props.listId

        if (!didDrop) {
            props.moveCard(listId, droppedId, originalIndex)
        }
    },
}

const cardTarget = {
    canDrop() {
        return false
    },

    hover(props: CardProps, monitor: DropTargetMonitor) {
        const { id: draggedId } = monitor.getItem()
        const { id: overId, listId} = props

        if (draggedId !== overId) {
            const { index: overIndex } = props.findCard(overId)
            props.moveCard(listId, draggedId, overIndex)
        }
    },
}

class Card extends React.Component {
    render() {
        const { text, isDragging, connectDragSource, connectDropTarget } = this.props
        let className = styles.Card
        let height = 0
        if(isDragging){
            className=styles.CardDragging
            height = this.myElement.clientHeight
        }

        return connectDragSource(
            connectDropTarget(<div className={className} ref={input => { this.myElement = input; }} style={{ height }}>{text}</div>),
        )
    }
}

export default DropTarget(Types.ITEM, cardTarget,
    connect => ({
        connectDropTarget: connect.dropTarget(),
    }),
)(
    DragSource(Types.ITEM, cardSource,
        (connect, monitor) => ({
            connectDragSource: connect.dragSource(),
            isDragging: monitor.isDragging(),
        }),
    )(Card))


