import React, {Component} from 'react';

import styles from './InputList.module.css';
import Aux from '../../hoc/Aux';

class InputList extends Component{
    state={
        listName: "",
        inputActive: false
    }

    handleChange = event => {
        this.setState({listName: event.target.value});
    }

    handleSubmit = event => {
        event.preventDefault();
        this.props.createNewList(this.state.listName);
        this.setState({listName: ""});
    }

    inputActiveHandler = () => {
        this.wrapperRef.current.focus();
        this.setState({inputActive: true});
    }

    handleClickOutside = (event) => {
        if (this.wrapperRef && !this.wrapperRef.contains(event.target)) {
            this.setState({inputActive: false});
        }
    }

    componentDidMount() {
        document.addEventListener('mousedown', this.handleClickOutside);
    }

    componentWillUnmount() {
        document.removeEventListener('mousedown', this.handleClickOutside);
    }

    render (){
        const formIsValid = this.state.listName.length > 0
        const listText = Object.keys(this.props.lists).length> 0 ? '+ Add another list' : '+ Add a list';
        let form  = (
                    <div ref={node => { this.wrapperRef = node; }} className={styles.InputList}>
                        <form onSubmit={this.handleSubmit}>
                            <input type="text" value={this.state.listName} onChange={this.handleChange} placeholder="Enter list title..."/>
                            <button  disabled={!formIsValid}>Add List</button>
                        </form>
                    </div>
            );

        if(!this.state.inputActive){
            form = <button onClick={this.inputActiveHandler} className={styles.InputListButton}>{listText}</button>
        }

        return(
            <Aux>
                {form}
            </Aux>
        );
    }
}

export default InputList;
