import React, {Component} from 'react';

import styles from './InputCard.module.css';

class InputCard extends Component{
    state={
        cardName: ""
    }

    handleChange = event => {
        this.setState({cardName: event.target.value});
    }

    handleSubmit = event => {
        event.preventDefault();
        this.props.createNewCard(this.state.cardName);
        this.setState({cardName: ""});
    }

    render (){
        const formIsValid = this.state.cardName.length > 0
        return(
            <div>
                <form onSubmit={this.handleSubmit} className={styles.InputList}>
                    <input type="text" value={this.state.cardName} onChange={this.handleChange} placeholder="Enter a title for this card..."/>
                    <button  disabled={!formIsValid}>Add Card</button>
                </form>
            </div>
        );
    }
}

export default InputCard;
