import React, {Component} from 'react';

import Button from '../../components/Button/Button';

import styles from './InputList.module.css';

class InputList extends Component{
    state={
        listName: "",
        formIsValid: true
    }

    handleChange = event => {
        this.setState({listName: event.target.value});
    }

    handleSubmit = event => {
        event.preventDefault();
        this.props.createNewList(this.state.listName);
    }

    render (){
        let form = (
            <form onSubmit={this.handleSubmit} className={styles.InputList}>
                <input type="text" value={this.state.listName} onChange={this.handleChange} placeholder="Enter list title..."/>
                <Button btnType='Success' disabled={!this.state.formIsValid}>Add List</Button>
                <button disabled={props.disabled} className={[styles.Button, styles[props.btnType]].join(' ')}
        onClick={props.clicked}>{props.children}</button>
            </form>
            );
        return(
            <div>
                {form}
            </div>
        );
    }
}

export default InputList;
