import * as React from 'react'
import { DragSource, DropTarget } from 'react-dnd'
import styles from './Card.module.css';

const Types = {
    ITEM: 'card'
}

const cardSource = {
    beginDrag(props) {
        return {
            id: props.id,
            originalIndex: props.findCard(props.id).index,
        }
    },

    endDrag(props, monitor) {
        const { id: droppedId, originalIndex } = monitor.getItem()
        const didDrop = monitor.didDrop()
        const currentList = props.listId

        if (!didDrop) {
            props.moveCard(currentList, droppedId, originalIndex)
        }
    },
}

const cardTarget = {
    canDrop() {
        return false
    },

    hover(props, monitor) {
        const { id: draggedId } = monitor.getItem()
        const { id: overId } = props
        const currentList = props.listId

        if (draggedId !== overId) {
            const { index: overIndex } = props.findCard(overId)
            props.moveCard(currentList, draggedId, overIndex)
        }
    },
}

const collectDrop = (connect) => {
    return {
        connectDropTarget: connect.dropTarget()
    }
}

const collectDrag = (connect, monitor) =>{
    return {
        connectDragSource: connect.dragSource(),
        isDragging: monitor.isDragging()
    }
}

class Card extends React.Component{
    render() {
        const { isDragging, connectDragSource, connectDropTarget} = this.props
        let className = styles.Card
        if(isDragging){
            className=styles.CardDragging
        }
        return connectDragSource(
            connectDropTarget(
                <div className={className}>
                    {this.props.cardName}
                </div>
            )
        )
    }
}

export default DropTarget(Types.ITEM, cardTarget, collectDrop)(DragSource(Types.ITEM, cardSource, collectDrag)(Card))
