import * as React from 'react'
import { DropTarget } from 'react-dnd'
import Card from '../../components/Card/Card'
import InputCard from '../InputCard/InputCard';
import styles from './List.module.css';

import { connect } from 'react-redux';
import * as listActions from '../../store/actions/lists';

const Types = {
    ITEM: 'card'
}

const cardTarget = {
    drop() {}
}

const collectDrop = (connect, monitor) =>{
    return {
        connectDropTarget: connect.dropTarget()
    }
}

class Container extends React.Component{
    render() {
        const { connectDropTarget } = this.props
        const { cards } = this.props

        return connectDropTarget(
            <div className={styles.List}>
                <h4>{this.props.listName}</h4>
                {cards.map(card => (
                    <Card
                        key={card.id}
                        id={`${card.id}`}
                        text={card.text}
                        listId={this.props.listId}
                        moveCard={this.moveCard}
                        findCard={this.findCard}
                    />
                ))}
                <InputCard createNewCard={this.props.onCardAdded} 
                    cards={this.props.cards} 
                    list={this.props.listName}/>
            </div>
        )
    }

    moveCard = (listId, id, atIndex) => {
        const { card, index } = this.findCard(id)
        this.props.onCardMovedInList(listId, card, index, atIndex);
    }

    findCard = (id) => {
        const { cards } = this.props
        const card = cards.filter(c => `${c.id}` === id)[0]

        return {
            card,
            index: cards.indexOf(card),
        }
    }
}

const mapDispatchToProps= dispatch => {
    return {
        onCardAdded: (cardName, listName) => dispatch(listActions.addCard(cardName, listName)),
        onCardMovedInList: (listId, cardName, currentIndex, newIndex) => dispatch(listActions.moveCardInList(listId, cardName, currentIndex, newIndex))

    }
}

export default DropTarget(Types.ITEM, cardTarget, collectDrop)(connect(null, mapDispatchToProps)(Container))
