import React, { Component } from 'react';
import { connect } from 'react-redux';
import styles from './Lists.module.css';
import List from '../List/List';
import InputList from '../InputList/InputList';
import * as listActions from '../../store/actions/lists';

class Lists extends Component {
    state={
        lists: {}
    }

    updateListState = (listName) => {
        let newLists= {...this.state.lists};
        newLists[listName]=[]
        this.setState({lists: newLists});
    }

    updateCardState = (cardName, listName) => {
        let newCards={...this.state.lists};
        newCards[listName].push(cardName);
        this.setState({cards: newCards});
    }

    render(){
        const displayLists = Object.keys(this.state.lists).map( listKeys => {
                return <List 
                    key={listKeys} 
                    listName={listKeys} 
                    cards={this.state.lists[listKeys]} 
                    createNewCard={(cardName, listName)=> this.updateCardState(cardName, listName)}/>;
            })

        return(
            <div className={styles.Lists}>
                {displayLists}
                <InputList createNewList={(listName)=> this.updateListState(listName)} lists={this.state.lists}/>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {
        lists: state.lists,
    };
}
const mapDispatchToProps= dispatch => {
    return {
        onListAdded: (listName) => dispatch(listActions.addList(listName)),
        onListRemoved: (listName) => dispatch(listActions.removeList(listName))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Lists);
