import React, { Component } from 'react';
import { connect } from 'react-redux';
import styles from './List.module.css';
import Card from '../../components/Card/Card';
import CardDrop from '../../components/CardDrop/CardDrop';
import InputCard from '../InputCard/InputCard';
import * as listActions from '../../store/actions/lists';

class List extends Component {

    render(){
        const displayCards = this.props.cards.map((cardName, index)=> {
            return <Card key={cardName+index} 
                        cardName={cardName} 
                        currentList={this.props.listName} 
                        moveCardToList={this.props.onCardMoved}/>
        })

        var dropFields = [];
        this.props.cards.forEach(function (officer) {
          officersIds.push(officer.id);
        });

        return(
           <div className={styles.List}>
                <h4>{this.props.listName}</h4>
                {displayCards}
                <CardDrop droppedList={this.props.listName}/>
                <InputCard createNewCard={this.props.onCardAdded} 
                            cards={this.props.cards} 
                            list={this.props.listName}/>
            </div>
        );
    }
}

const mapDispatchToProps= dispatch => {
    return {
        onCardAdded: (cardName, listName) => dispatch(listActions.addCard(cardName, listName)),
        onCardMoved: (cardName, currentList, droppedList) => dispatch(listActions.moveCard(cardName, currentList, droppedList)),
    }
}

export default connect(null, mapDispatchToProps)(List);
