import * as React from 'react'
import { DropTarget } from 'react-dnd'
import Card from '../../components/Card/Card'
import InputCard from '../InputCard/InputCard';
import styles from './List.module.css';

import { connect } from 'react-redux';
import * as listActions from '../../store/actions/lists';

const Types = {
    ITEM: 'card'
}

const cardTarget = {
    drop() {}
}

const collectDrop = (connect, monitor) =>{
    return {
        connectDropTarget: connect.dropTarget()
    }
}

class Container extends React.Component{
    render() {
        const { connectDropTarget } = this.props
        const { cards } = this.props
        let displayCards = null
        if(cards){
            displayCards = cards.map(card => {
                return <Card
                    key={card.id}
                    id={`${card.id}`}
                    index={cards.indexOf(card)}
                    text={card.text}
                    listId={this.props.listId}
                    moveCard={this.moveCard}
                    findCard={this.findCard}
                    findList={this.findList}
                />;
            })
        }

        return connectDropTarget(
            <div className={styles.List}>
                <h4>{this.props.listName}</h4>
                {displayCards}
                <InputCard createNewCard={this.props.onCardAdded} 
                    cards={this.props.cards} 
                    listId={this.props.listId}/>
            </div>
        )
    }

    moveCard = (originalListId, id, atIndex) => {
        const { card, index } = this.findCard(id, originalListId)
        const newListId = this.findList(id, originalListId)


        this.props.onCardMovedInList(newListId, originalListId, index, atIndex, card);
    }

    findCard = (id, list) => {
        const { cards } = this.props.lists[list]
        let card = cards.filter(c => `${c.id}` === id)[0]
        if(!card){
            card = cards[cards.length-1]
        }
        return {
            card,
            index: cards.indexOf(card),
        }
    }

    findList = (id, list) => {
        const { cards } = this.props.lists[list]
        let card = cards.filter(c => `${c.id}` === id)[0]
        if(!card){
            card = cards[cards.length-1]
        }

        for (const [key, value] of Object.entries(this.props.lists)) {
            for ( const currentCard in value.cards) {
                if(value.cards[currentCard]===card){
                    return key;
                }
            }
        }
    }
}

const mapDispatchToProps= dispatch => {
    return {
        onCardAdded: (cardName, cards, listId) => dispatch(listActions.addCard(cardName, cards, listId)),
        onCardMovedInList: (newListId, originalListId, currentIndex, newIndex, card) => dispatch(listActions.moveCardInList(newListId, originalListId, currentIndex, newIndex, card))

    }
}

export default DropTarget(Types.ITEM, cardTarget, collectDrop)(connect(null, mapDispatchToProps)(Container))
