import React, { Component } from 'react';
import { findDOMNode } from 'react-dom';
import styles from './Card.module.css';
import flow from 'lodash/flow';

import { DragSource, DropTarget } from 'react-dnd'
// import { XYCoord } from 'dnd-core'

const Types = {
    ITEM: 'card'
}

const itemSource = {
    beginDrag(props) {
        return {
            id: props.id
        }
    },
}

const itemTarget = {
    hover(props, monitor, component) {
        if (!component) {
            return null
        }
        const dragIndex = monitor.getItem().id
        const hoverIndex = props.id

        // Don't replace items with themselves
        if (dragIndex === hoverIndex) {
            return
        }

        // Determine rectangle on screen
        const hoverBoundingRect = (findDOMNode(component)).getBoundingClientRect()

        // Get vertical middle
        const hoverMiddleY = (hoverBoundingRect.bottom - hoverBoundingRect.top) / 2

        // Determine mouse position
        const clientOffset = monitor.getClientOffset()

        // Get pixels to the top
        const hoverClientY = (clientOffset).y - hoverBoundingRect.top

        // Only perform the move when the mouse has crossed half of the items height
        // When dragging downwards, only move when the cursor is below 50%
        // When dragging upwards, only move when the cursor is above 50%

        // Dragging downwards
        if (dragIndex < hoverIndex && hoverClientY < hoverMiddleY) {
            console.log("down and out of bounds");
            return
        }

        console.log("dragIndex:", dragIndex);
        console.log("hoverIndex:", hoverIndex);
        console.log("hoverClientY:", hoverClientY);
        console.log("hoverMiddleY:", hoverMiddleY);

        // Dragging upwards
        if (dragIndex > hoverIndex && hoverClientY > hoverMiddleY) {
            console.log("up and out of bounds");
            return
        }

        // Time to actually perform the action
        props.moveCard(dragIndex, hoverIndex)

        // Note: we're mutating the monitor item here!
        // Generally it's better to avoid mutations,
        // but it's good here for the sake of performance
        // to avoid expensive index searches.
        monitor.getItem().index = hoverIndex
    },
}

const collectDrag = (connect, monitor) =>{
    return {
        connectDragSource: connect.dragSource(),
        isDragging: monitor.isDragging()
    }
}

const collectDrop = (connect, monitor) => {
    return {
        connectDropTarget: connect.dropTarget()
    }
}

class Card extends Component {

    render() {
        const { isDragging, connectDragSource, connectDropTarget} = this.props
        let className = styles.Card
        if(isDragging){
            className=styles.CardDragging
        }
        return connectDragSource(
            connectDropTarget(
                <div className={className}>
                    {this.props.cardName}
                </div>
            )
        )
    }
}

export default flow(
    DragSource(Types.ITEM, itemSource, collectDrag),
    DropTarget(Types.ITEM, itemTarget, collectDrop)
)(Card)
