import * as actionTypes from '../actions/actionTypes';

const initialState = {
    lists: {
        1:{
            name: "first List",
            cards: [
                {
                    id: 1,
                    text: 'Write a cool JS library',
                },
                {
                    id: 2,
                    text: 'Make it generic enough',
                },
                {
                    id: 3,
                    text: 'Write README',
                },
                {
                    id: 4,
                    text: 'Create some examples',
                },
                {
                    id: 5,
                    text: 'Spam in Twitter and IRC to promote it',
                },
                {
                    id: 6,
                    text: '???',
                },
                {
                    id: 7,
                    text: 'PROFIT',
                }
            ]
        }
    }
};

const reducer = (state=initialState, action) =>{
    switch(action.type){
        case actionTypes.ADD_LIST:
            return{
                ...state,
                lists: {
                    ...state.lists, 
                    [action.listName]: []
                }
            };
        case actionTypes.REMOVE_LIST:
            return{
                ...state,
                lists:  [
                    ...state.lists.slice(0, state.lists.indexOf(action.listName)),
                    ...state.lists.slice(state.lists.indexOf(action.listName)+1)
                ]
            };
        case actionTypes.ADD_CARD:
            return{
                ...state,
                lists: {
                    ...state.lists,
                   [action.listName]: [
                        ...state.lists[action.listName],
                        action.cardName
                   ]
                }
            };
        case actionTypes.MOVE_CARD:
            const updatedList = {...state.lists[action.listId]}
            const updatedListCards = [...updatedList.cards]
            const draggedCard = {...updatedListCards[action.currentIndex]}
            const droppedCard = {...updatedListCards[action.newIndex]}
            console.log("Updated a list:", updatedListCards, draggedCard, droppedCard);
            [draggedCard.id, droppedCard.id] = [droppedCard.id, draggedCard.id]
            updatedListCards[draggedCard.id-1]= draggedCard
            updatedListCards[droppedCard.id-1]= droppedCard
            return{
                ...state,
                lists: {
                    ...state.lists,
                    [action.listId]:{
                        ...state.lists[action.listId],
                        cards: updatedListCards
                    }
                }
            };
        default:
            return state;
    }
}

export default reducer;
