import React, { Component } from 'react';
import styles from './List.module.css';
import Card from '../../components/Card/Card';
import InputCard from '../InputCard/InputCard';

class List extends Component {
    state={
        cards: []
    }

    updateCardState = (cardName) => {
        let newCards=[...this.state.cards];
        newCards.push(cardName);
        this.setState({cards: newCards});
    }

    render(){
        let displayCards = []
        if (this.state.cards.length > 0){
            this.state.cards.map((cardName, index) => {
                    displayCards.push(<Card key={cardName+index} cardName={cardName}/>);
                })
        }

        return(
           <div className={styles.List}>
                <h4>{this.props.listName}</h4>
                {displayCards}
                <InputCard createNewCard={(cardName)=> this.updateCardState(cardName)}/>
            </div>
        );
    }
}

export default List;
