import React, {Component} from 'react';

import styles from './InputList.module.css';
import Aux from '../../hoc/Aux';

import onClickOutside from "react-onclickoutside";

class InputList extends Component{
    state={
        listName: "",
        inputActive: false
    }

    handleChange = event => {
        this.setState({listName: event.target.value});
    }

    handleSubmit = event => {
        event.preventDefault();
        this.props.createNewList(this.state.listName);
        this.setState({listName: ""});
    }

    inputActiveHandler = () => {
        this.setState({inputActive: true});
    };

    handleClickOutside = event => {
        console.log("YOU CLICKED OUTSIDE");
    }

    render (){
        const formIsValid = this.state.listName.length > 0
        let form = <button onClick={this.inputActiveHandler} className={styles.InputListButton}>+ Add another list</button>

        if(this.state.inputActive){
            form  = (
                    <div className={styles.InputList}>
                        <form onSubmit={this.handleSubmit}>
                            <input type="text" value={this.state.listName} onChange={this.handleChange} placeholder="Enter list title..."/>
                            <button  disabled={!formIsValid}>Add List</button>
                        </form>
                    </div>
            );
        }
        return(
            <Aux>
                {form}
            </Aux>
        );
    }
}

export default InputList;
