import React, { Component } from 'react';
import styles from './Lists.module.css';
import List from '../List/List';

class Lists extends Component {
    state={
        lists: null
    }

    render(){

        let displayLists = Object.keys(this.state.lists)
            .map(listName => {
                return <List key={listName} name={listName}/>
            })
            .reduce((arr, el) => {
                return arr.concat(el)
            }, []);
        if (displayLists.length === 0){
            displayLists = <p> Please start adding the ingredients! </p>
        }

        return(
            <div className={styles.Lists}>
                {displayLists}
                <div className={styles.InputList}>+ Add Another List</div>
            </div>
        );
    }
}

export default Lists;
