import * as actionTypes from '../actions/actionTypes';

const initialState = {
    lists: {
        1:{
            name: "first List",
            cards: [
                {
                    id: 1,
                    text: 'Write a cool JS library',
                },
                {
                    id: 2,
                    text: 'Make it generic enough',
                },
                {
                    id: 3,
                    text: 'Write README',
                },
                {
                    id: 4,
                    text: 'Create some examples',
                },
                {
                    id: 5,
                    text: 'Spam in Twitter and IRC to promote it',
                },
                {
                    id: 6,
                    text: '???',
                },
                {
                    id: 7,
                    text: 'PROFIT',
                }
            ]
        }
    }
};

const reducer = (state=initialState, action) =>{
    switch(action.type){
        case actionTypes.ADD_LIST:
            return{
                ...state,
                lists: {
                    ...state.lists, 
                    [action.listName]: []
                }
            };
        case actionTypes.REMOVE_LIST:
            return{
                ...state,
                lists:  [
                    ...state.lists.slice(0, state.lists.indexOf(action.listName)),
                    ...state.lists.slice(state.lists.indexOf(action.listName)+1)
                ]
            };
        case actionTypes.ADD_CARD:
            return{
                ...state,
                lists: {
                    ...state.lists,
                   [action.listName]: [
                        ...state.lists[action.listName],
                        action.cardName
                   ]
                }
            };
        case actionTypes.MOVE_CARD:
            console.log(action.listId)
            const updatedList = {...state.lists}
            const firstList = updatedList[action.listId]
            // const draggedCard = {...updatedList[action.currentIndex]}
            // const droppedCard = {...updatedList[action.newIndex]}
            // updatedList.splice(action.newIndex, 0, updatedList.splice(action.currentIndex, 1)[0]);
            // console.log("Updated a list:", updatedList, firstList);
            return{
                ...state,
                lists: {
                    ...state.lists
                }
            };
        default:
            return state;
    }
}

export default reducer;
