import * as actionTypes from '../actions/actionTypes';

const initialState = {
    lists: {
        1:{
            name: "first List",
            cards: [
                {
                    id: 1,
                    text: 'Write a cool JS library',
                },
                {
                    id: 2,
                    text: 'Make it generic enough',
                },
                {
                    id: 3,
                    text: 'Write README',
                },
                {
                    id: 4,
                    text: 'Create some examples',
                },
                {
                    id: 5,
                    text: 'Spam in Twitter and IRC to promote it',
                },
                {
                    id: 6,
                    text: '???',
                },
                {
                    id: 7,
                    text: 'PROFIT',
                }
            ]
        }
    }
};

const reducer = (state=initialState, action) =>{
    switch(action.type){
        case actionTypes.ADD_LIST:
            const newList = {name: action.listName, cards:[]};
            return{
                ...state,
                lists: {
                    ...state.lists,
                    [action.listLength]: newList
                }
            };
        case actionTypes.REMOVE_LIST:
            return{
                ...state,
                lists:  [
                    ...state.lists.slice(0, state.lists.indexOf(action.listName)),
                    ...state.lists.slice(state.lists.indexOf(action.listName)+1)
                ]
            };
        case actionTypes.ADD_CARD:
            const newCard = {id: action.cardLength+1, text: action.cardName}
            return{
                ...state,
                lists: {
                    ...state.lists,
                   [action.listId]: {
                        ...state.lists[action.listId],
                        cards:[
                            ...state.lists[action.listId].cards,
                            newCard
                        ]
                    }
                }
            };
        case actionTypes.MOVE_CARD:
            console.log();

            const updatedList = {...state.lists[action.listId]}
            const updatedListCards = [...updatedList.cards]
            updatedListCards.splice(action.newIndex, 0, updatedListCards.splice(action.currentIndex, 1)[0])
            // val.splice(to, 0, val.splice(from, 1)[0])
            return{
                ...state,
                lists: {
                    ...state.lists,
                    [action.listId]:{
                        ...state.lists[action.listId],
                        cards: updatedListCards
                    }
                }
            };
        default:
            return state;
    }
}

export default reducer;
