import React, { Component } from 'react';
import styles from './Lists.module.css';
import List from '../List/List';
import InputList from '../InputList/InputList';

class Lists extends Component {
    state={
        lists: []
    }

    updateListState = (listName) => {
        let newLists= [...this.state.lists];
        newLists.push(listName);
        this.setState({lists: newLists});
    }

    render(){
        let displayLists = []
        if (this.state.lists.length > 0){
            this.state.lists.map((listName, index) => {
                    displayLists.push(<List key={listName+index} listName={listName}/>);
                })
        }

        return(
            <div className={styles.Lists}>
                {displayLists}
                <InputList createNewList={(listName)=> this.updateListState(listName)}/>
            </div>
        );
    }
}

export default Lists;
