import React, { Component } from 'react';
import styles from './Lists.module.css';
import List from '../List/List';
import InputList from '../InputList/InputList';

class Lists extends Component {
    state={
        lists: {"First list":["faith", "family", "gun"], 
                "SEcond List":[]}
    }

    updateListState = (listName) => {
        let newLists= {...this.state.lists};
        newLists[listName]=[]
        this.setState({lists: newLists});
    }

    updateCardState = (cardName) => {
        console.log("cardName:", cardName);
        let newCards={...this.state.lists};
        newCards.push(cardName);
        this.setState({cards: newCards});
    }

    render(){
        const displayLists = Object.keys(this.state.lists).map( listKeys => {
            console.log("listKeys:", listKeys);
                return <List 
                    key={listKeys} 
                    listName={listKeys} 
                    cards={this.state.lists[listKeys]} 
                    createNewCard={(cardName)=> this.updateCardState(cardName)}/>;
            })

        return(
            <div className={styles.Lists}>
                {displayLists}
                <InputList createNewList={(listName)=> this.updateListState(listName)}/>
            </div>
        );
    }
}

export default Lists;
