import * as React from 'react'
import { DropTarget } from 'react-dnd'
import Card from '../../components/Card/Card'
import { connect } from 'react-redux';
import * as listActions from '../../store/actions/lists';

import update from 'immutability-helper';

const Types = {
    ITEM: 'card'
}

const style = {
    width: 400
}

const cardTarget = {
    drop() {}
}

const collectDrop = (connect, monitor) =>{
    return {
        connectDropTarget: connect.dropTarget()
    }
}

class Container extends React.Component{
    state = {
        cards: [
            {
                id: 1,
                text: 'Write a cool JS library',
            },
            {
                id: 2,
                text: 'Make it generic enough',
            },
            {
                id: 3,
                text: 'Write README',
            },
            {
                id: 4,
                text: 'Create some examples',
            },
            {
                id: 5,
                text: 'Spam in Twitter and IRC to promote it',
            },
            {
                id: 6,
                text: '???',
            },
            {
                id: 7,
                text: 'PROFIT',
            },
        ],
    }

    render() {
        const { connectDropTarget } = this.props

        return connectDropTarget(
            <div style={style}>
                {this.state.cards.map(card => (
                    <Card
                        key={card.id}
                        id={`${card.id}`}
                        text={card.text}
                        moveCard={this.moveCard}
                        findCard={this.findCard}
                    />
                ))}
            </div>
        )
    }

    moveCard = (id: string, atIndex: number) => {
        const { card, index } = this.findCard(id)
        this.setState(
            update(this.state, {
                cards: {
                    $splice: [[index, 1], [atIndex, 0, card]],
                },
            }),
        )
    }

    findCard = (id: string) => {
        const { cards } = this.state
        const card = cards.filter(c => `${c.id}` === id)[0]

        return {
            card,
            index: cards.indexOf(card),
        }
    }
}

const mapDispatchToProps= dispatch => {
    return {
        onCardAdded: (cardName, listName) => dispatch(listActions.addCard(cardName, listName)),
        onCardMovedInList: (listName, cardName, currentIndex, newIndex) => dispatch(listActions.moveCardInList(listName, cardName, currentIndex, newIndex))
        // onCardMovedBetweenLists: (cardName, currentList, droppedList) => dispatch(listActions.moveCard(cardName, currentList, droppedList)),
    }
}

export default DropTarget(Types.ITEM, cardTarget, collectDrop)(connect(null, mapDispatchToProps)(Container))


