import React, {Component} from 'react';

import styles from './InputCard.module.css';

class InputCard extends Component{
    state={
        cardName: "",
        inputActive: false
    }

    handleChange = event => {
        this.setState({cardName: event.target.value});
    }

    handleSubmit = event => {
        event.preventDefault();
        this.props.createNewCard(this.state.cardName);
        this.setState({cardName: ""});
    }

    inputActiveHandler = () => {
        this.setState({inputActive: true});
    }

    handleClickOutside = (event) => {
        if (this.wrapperRef && !this.wrapperRef.contains(event.target)) {
            this.setState({inputActive: false});
        }
    }

    componentDidMount() {
        document.addEventListener('mousedown', this.handleClickOutside);
    }

    componentWillUnmount() {
        document.removeEventListener('mousedown', this.handleClickOutside);
    }

    render (){
        const formIsValid = this.state.cardName.length > 0
        const listText = Object.keys(this.props.lists).length> 0 ? '+ Add another list' : '+ Add a list';
        return(
            <div  className={styles.InputCard}>
                <form onSubmit={this.handleSubmit}>
                    <input type="text" value={this.state.cardName} onChange={this.handleChange} placeholder="Enter card title..."/>
                    <button  disabled={!formIsValid}>Add Card</button>
                </form>
            </div>
        );
    }
}

export default InputCard;
